package com.finvendor.common.exception;import java.io.Serializable;/** *  * Super Type of all Application unchecked excpetion (abstract class), To be sub * classed by technical exception only *  * @author Ayush */public abstract class FvRootRuntimeException extends RuntimeException  implements Serializable {	private static final long serialVersionUID = 1L;	public FvRootRuntimeException() {		super();	}	public FvRootRuntimeException(final String message) {		super(message);	}	public FvRootRuntimeException(final Throwable cause) {		super(cause);	}	public FvRootRuntimeException(final String message, final Throwable cause) {		super(message, cause);	}	final public Exception getOriginalCause() {		Throwable res = this;		Throwable e = this;		while ((e = e.getCause()) != null) {			res = e;		}		return (Exception) res;	}}