package com.finvendor.common.exception;/** * Application checked excpetion for technical issue, Use this class by default * or one of his subclass *  * @author Ayush */public class FvTechnicalException extends FvRootException {	private static final long serialVersionUID = 1L;	public FvTechnicalException() {		super();	}	public FvTechnicalException(final String message) {		super(message);	}	public FvTechnicalException(final Throwable cause) {		super(cause);	}	public FvTechnicalException(final String message, final Throwable cause) {		super(message, cause);	}}